sp_widget.client_script:
api.controller=function() {
	/* widget controller */

	var c = this;

	$("#calendario").fullCalendar({
		events: c.data.listNotes,
		header:{
			left: 'title',
			center: '',
			right: 'today prevYear,prev,next,nextYear'
		}
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.listNotes = [];

	var grNotes = new GlideRecord('x_snc_createnotes_note');
	grNotes.query();

	while(grNotes.next()){
		var objNotes = {};
		/*objNotes.sys_id = grNotes.getValue('sys_id');
		objNotes.number = grNotes.getDisplayValue('number');
		objNotes.title = grNotes.getDisplayValue('title');
		objNotes.user = grNotes.getDisplayValue('user');*/
		objNotes.created = grNotes.getValue('sys_created_on');		
		data.listNotes.push(objNotes);
	}

})();
